mapper.select:
SELECT psNo, uuid, uploadPath, fileName
FROM pop_img_tbl
WHERE psNo = #{psNo}

SELECT count(*) FROM like_list_tbl
WHERE psNo = #{psNo} AND
userNo = #{userNo}

SELECT
psNo FROM pop_main_tbl
WHERE psName = #{storeName}

SELECT NVL(AVG(psScore), 0)
FROM pop_reply_tbl
WHERE psNo = #{psNo}

SELECT COUNT(*)
FROM like_list_tbl
WHERE userNo = #{userNo} AND psNo = #{psNo}
